                            "x2":"-90.5061779","y2":"14.6048341",
                            "x3":"-90.506124","y3":"14.604449",
                            "x4":"-90.50624299","y4":"14.60484282"
                        },
                        "hint1":"hola1",
                        "hint2":"hola2",
                        "pregunta":"Donde es esto",
                        "name":"Estacion misteriosa"
                    }]
                }
             }
         }

    componentDidMount() {
        this.getLocation();
    }


    handleSubmit = (event) => {
                 event.prevenDefault()
                 const data = this.state
                 console.log("Data que se esta enviando: ", data)
            }
             handleInputChange = (event) => {
                 event.preventDefault()
                 this.setState({
                     [event.target.name]: event.target.value
                 })
             }

    startGame() {
        const data = this.state.data
        const stations = data.stations
        for(let i = stations.length - 1; i > 0; i--){
            const j = Math.floor(Math.random() * i)
            const temp = stations[i]
           stations[i] = stations[j]
            stations[j] = temp
        }
        console.log("suffle", data)
        // data.stations = suffle
        this.setState({
            ...this.state,
            started: true,
            data
        })
        console.log(this.state)
    }


    getLocation(){
        var msg;

        if('geolocation' in navigator){
            var options = {
                enableHighAccuracy: false,
                timeout: 5000,
                maximumAge: 0
            };

            navigator.geolocation.getCurrentPosition(position => {
                this.setState({
