        }

  }


    showHint1() {
        const current = this.state.current
        const hint = this.state.data.stations[current].hint1
        alert(hint)
    }

    showHint2() {
        const current = this.state.current
        const hint = this.state.data.stations[current].hint2
        alert(hint)
    }

    nextStation() {
        const actual = this.state.current
        this.setState({
            ...this.state,
            current: actual +1
         })
        this.renderStation()
    }

    renderStation() {

        const currentp = this.state.current
        const station = this.state.data.stations[currentp]
        return (
            <div >
                <div >      
        
                {/*CUENTA */}
                <Grid container alignItems="center" >
                    <PersonIcon color="secondary" />
                    <Typography gutterBottom variant="h6">
                       Estacion {station.name}
                    </Typography>
                </Grid>
                <div id="hints">
                    <Button variant="outlined" color="primary" onClick={() => this.showHint1()}> Primera hint</Button>
                    <Button variant="outlined" color="primary" onClick={() => this.showHint2()}> Segunda hint</Button>
                </div>
              </div>
                <Footer />
            </div>
            );

    }


    render() {
        return (
            <div>
               { !this.state.started ?
                <Button variant="outlined" color="primary" onClick={() => this.startGame()}> Start Game </Button>
                : this.renderStation()
               }
            </div>
        )
    }
  }

  export default Game;
