  calcQ(q) {
    const when = this.state.start
    const now = moment()
    const diff = now.diff(when, 'minutes')
    console.log("diferencia", diff)
    const calc = Math.floor(((60-diff)/60)*q) ///DUDAAAA
    return calc
}



