import React, { Component } from 'react';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import PersonIcon from '@material-ui/icons/Person';
import LocalAtmIcon from '@material-ui/icons/LocalAtm';
import Button from '@material-ui/core/Button';
import AccountTreeIcon from '@material-ui/icons/AccountTree';
import Footer from '../Footer';
import { geolocated } from "react-geolocated";

class Game extends Component {
    
         constructor(props){
             super(props)
             this.state = {
                 codigo: ''
             }
         }

        componentDidMount() {
            this.getLocation();
        }


         handleSubmit = (event) => {
             event.prevenDefault()
             const data = this.state
             console.log("Data que se esta enviando: ", data)
     }
         handleInputChange = (event) => {
             event.preventDefault()
             this.setState({
                 [event.target.name]: event.target.value
             })
         }


    getLocation(){
    var msg;

    if('geolocation' in navigator){
      requestLocation();
    }else{
      // no geolocation :(
      msg = "Sorry, looks like your browser doesn't support geolocation";
      console.log(msg)
    }
    /*** 
    requestLocation() returns a message, either the users coordinates, or an error message
    **/
    function requestLocation(){
      /**
      getCurrentPosition() below accepts 3 arguments:
      a success callback (required), an error callback  (optional), and a set of options (optional)
      **/
      var options = {
        // enableHighAccuracy = should the device take extra time or power to return a really accurate result, or should it give you the quick (but less accurate) answer?
        enableHighAccuracy: false,
        // timeout = how long does the device have, in milliseconds to return a result?
        timeout: 5000,
        // maximumAge = maximum age for a possible previously-cached position. 0 = must return the current position, not a prior cached position
        maximumAge: 0
      };
      // call getCurrentPosition()
      navigator.geolocation.getCurrentPosition(success, error, options); 
      // upon success, do this
      function success(pos){
        // get longitude and latitude from the position object passed in
        var lng = pos.coords.longitude;
        var lat = pos.coords.latitude;
        // and presto, we have the device's location!
        msg = 'You appear to be at longitude: ' + lng + ' and latitude: ' + lat  + '<img src="https://maps.googleapis.com/maps/api/staticmap?zoom=15&size=300x300&maptype=roadmap&markers=color:red%7Clabel:A%7C' + lat + ',' + lng+ '&sensor=false">'
        console.log(msg)
    }
      // upon error, do this
      function error(err){
        // return the error message
        msg = 'Error: ' + err + ' :(';
        console.log(msg)
      }
    } // end requestLocation();
  }


    render() {

        return (
            <div >
                
                <div >
                    
        
                <div >
                <Grid container alignItems="center">
                  <Grid item xs>
                    <Typography gutterBottom>
                    Estacion no. 1
                    </Typography>
                    <Typography gutterBottom >
                    Ve hacia donde se oye "quak"
                    </Typography>
                  </Grid>
                  <Grid item>
                    <Typography gutterBottom>
                    
                    </Typography>
                  </Grid>
                </Grid>
        
                {/*CUENTA */}
                <Grid container alignItems="center" >
                    <Grid item xs={1}>
                    <PersonIcon color="secondary" />
                    </Grid>
                    <Grid item>
                        <Typography gutterBottom variant="h6">
                        Estacion no. 1
                        </Typography>
                    </Grid>
                </Grid>
        
                <Button variant="outlined" color="primary"> Primera hint</Button>
                <Button variant="outlined" color="primary"> Segunda hint</Button>
        
                {/* <Form onSubmit={this.handleSubmit}>
                    <input type='text' placeholder='Ingrese codigo' value={name} name='codigo' onChange={this.handleInputChange}/>
                    <button>Enviar codigo</button>
                </Form> */}
                
              </div>
        
                </div>
                <Footer />
            </div>
            );

    }
  }

  export default Game;
